import { Component, OnInit } from '@angular/core';
import { Medicamento } from 'src/app/models/medicamento';
import { MedicamentoService } from 'src/app/services/medicamento.service';
import { FormGroup, Validators, FormBuilder } from '@angular/forms';
import { VentaService } from 'src/app/services/venta.service';

@Component({
  selector: 'app-medicamento',
  templateUrl: './medicamento.component.html',
  styleUrls: ['./medicamento.component.css']
})
export class MedicamentoComponent implements OnInit{

  medicamentos: Medicamento[] = [];
  cols: any[] = [];
  msgs: any[] = [];
  modalMed: boolean = false;
  modalMMed: boolean = false;
  modalVenta: boolean = false;
  frmMedicamento: any;
  frmMMedicamento: any;
  medVenta: any;
  precio: number = 0;
  medicamento: any = {
    id: null,
    nombre: null,
    fecha_fabricacion: null,
    fecha_vencimiento: null,
    stock: null,
    valor: null
  }
  venta: any = {
    fecha: null,
    hora: null,
    medicamento: null,
    cantidad: null,
    valor_unitario: null,
    valor_total: null
  };

  constructor(private medicamentoService: MedicamentoService, private ventaService: VentaService, private formBuilder: FormBuilder) {}

  ngOnInit(): void {
    this.getMedicamentos();
    this.cols = [
      {fields: "id", header: "Id"},
      {fields: "nombre", header: "Nombre"},
      {fields: "fecha_fabricacion", header: "Fecha de fabricacion"},
      {fields: "fecha_vencimiento", header: "Fecha de vencimiento"},
      {fields: "stock", header: "Cantidad"},
      {fields: "valor", header: "Valor Unitario"},
    ]
    this.frmMedicamento = this.formBuilder.group({
      nombre: ['', [Validators.required, Validators.minLength(3)]],
      fecha_fabricacion: ['', [Validators.required]],
      fecha_vencimiento: ['', [Validators.required]],
      stock: ['', [Validators.required, Validators.min(1)]],
      valor: ['', [Validators.required, Validators.min(100)]],
    });
    this.frmMMedicamento = this.formBuilder.group({
      id: ['', [Validators.required]],
      nombre: ['', [Validators.required, Validators.minLength(3)]],
      fecha_fabricacion: ['', [Validators.required]],
      fecha_vencimiento: ['', [Validators.required]],
      stock: ['', [Validators.required, Validators.min(1)]],
      valor: ['', [Validators.required, Validators.min(100)]],
    });
  }

  getMedicamentos(){
    this.medicamentoService.getMedicamentos().subscribe( (result): any =>{
      let meds: Medicamento[] = [];
      for (const element of result) {
        element.fecha_fabricacion = this.formDate(element.fecha_fabricacion);
        element.fecha_vencimiento = this.formDate(element.fecha_vencimiento);
        meds.push(element);
      }
      this.medicamentos = meds;
    },
    error => {
      console.log(error);
    }
    );
  }

  showModalMed(): void{
    this.modalMed = true;
  }

  RegistrarMed(): void{
    let form = {
      nombre: this.frmMedicamento.controls['nombre'].value,
      fecha_fabricacion: new Date(`${this.frmMedicamento.controls['fecha_fabricacion'].value}T00:00:00`),
      fecha_vencimiento: new Date(`${this.frmMedicamento.controls['fecha_vencimiento'].value}T00:00:00`),
      stock: this.frmMedicamento.controls['stock'].value,
      valor: this.frmMedicamento.controls['valor'].value
    }
    this.medicamentoService.save(form).subscribe(res => {
      if(res){
        this.modalMed = false;
        this.frmMedicamento.reset();
        this.getMedicamentos();
      }
    })
  }

  editarMedicamento(medId: number): void{
    let med: any = this.medicamentos.find(ele => ele.id == medId);
    this.frmMMedicamento = this.formBuilder.group({
      id: [med.id, [Validators.required]],
      nombre: [med.nombre, [Validators.required, Validators.minLength(3)]],
      fecha_fabricacion: [med.fecha_fabricacion, [Validators.required]],
      fecha_vencimiento: [med.fecha_vencimiento, [Validators.required]],
      stock: [med.stock, [Validators.required, Validators.min(1)]],
      valor: [med.valor, [Validators.required, Validators.min(100)]],
    });
    console.log(med);
    this.modalMMed = true;
  }

  venderMedicamento(medId: number): void{
    this.medVenta = this.medicamentos.find(ele => ele.id == medId);
    this.precio = this.medVenta.valor;
    this.modalVenta = true;
  }

  ModificarMed(): void{
    let form = {
      id: this.frmMMedicamento.controls['id'].value,
      nombre: this.frmMMedicamento.controls['nombre'].value,
      fecha_fabricacion: new Date(`${this.frmMMedicamento.controls['fecha_fabricacion'].value}T00:00:00`),
      fecha_vencimiento: new Date(`${this.frmMMedicamento.controls['fecha_vencimiento'].value}T00:00:00`),
      stock: this.frmMMedicamento.controls['stock'].value,
      valor: this.frmMMedicamento.controls['valor'].value
    }
    this.medicamentoService.update(form).subscribe(res => {
      console.log(res);
      if(res){
        this.modalMMed = false;
        this.getMedicamentos();
      }
    })
  }

  eliminarMedicamento(medId: number){
    console.log(medId);
    this.medicamentoService.delete(medId).subscribe(res => {
      this.getMedicamentos();
    })
  }

  VenderMed(): void{
    if(this.venta.cantidad<=this.medVenta.stock && this.venta.cantidad>0){
      this.medVenta.stock -= this.venta.cantidad;
      this.medicamentoService.update(this.medVenta).subscribe(res => {
        if(res){
          this.getMedicamentos();
        }
      });
      let fecha = new Date();
      this.venta.fecha = fecha;
      this.venta.hora = fecha.getHours()+":"+fecha.getMinutes();
      this.venta.medicamento = this.medVenta.nombre;
      this.venta.valor_unitario = this.medVenta.valor;
      this.venta.valor_total = this.medVenta.valor * this.venta.cantidad;
      this.ventaService.save(this.venta).subscribe(res => {
        if(res){
          this.modalVenta = false;
        }
      })
    }else{
      this.msgs = [{severity:'error', summary:'Error', detail:'Stock insuficiente'}];
    }

  }

  formDate(date: any) {
    let fecha = new Date(date),
        month = '' + (fecha.getMonth() + 1),
        day = '' + fecha.getDate(),
        year = fecha.getFullYear();
    if (month.length < 2)
        month = '0' + month;
    if (day.length < 2)
        day = '0' + day;
    return [year, month, day].join('-');
  }
}
<div>
  <br>
  <button pButton type="button" label="Registrar nuevo" class="p-button-primary" (click)="showModalMed()"></button>
  <br>
  <br>
  <p-table [value]="medicamentos" [columns]="cols" [paginator]="true" [rows]="3" >
    <ng-template pTemplate="header" let-columns>
        <tr>
          <th *ngFor="let col of columns" scope="col">{{col.header}}</th>
            <th scope="col">Opciones</th>
        </tr>
    </ng-template>
    <ng-template pTemplate="body" let-medicamento let-columns="columns">
        <tr>
            <td *ngFor="let col of columns" >
              <span *ngIf="col.fields === 'fecha_fabricacion' || col.fields === 'fecha_vencimiento'; then thenTemplateName else elseTemplateName"></span>
              <ng-template #thenTemplateName>{{ medicamento[col.fields] | date: 'dd/MM/yyyy'}}</ng-template>
              <ng-template #elseTemplateName>{{ medicamento[col.fields]}}</ng-template>
            </td>
            <td>
              <button pButton type="button" label="Vender" class="p-button-primary" (click)="venderMedicamento(medicamento.id)" style="margin-right: 10px;"></button>
              <button pButton type="button" label="Editar" class="p-button-warning" (click)="editarMedicamento(medicamento.id)" style="margin-right: 10px;"></button>
              <button pButton type="button" label="Borrar" class="p-button-danger" (click)="eliminarMedicamento(medicamento.id)"></button>
            </td>
        </tr>
    </ng-template>
  </p-table>
  <!--MODAL REGISTRO-->
  <p-dialog header="Registrar nuevo" [responsive]="true" [style]="{width: '550px'}" [(visible)]="modalMed" [modal]="true" styleClass="p-fluid">
    <form [formGroup]="frmMedicamento" class="login-container" (submit)="RegistrarMed()">
      <div>
        <label for="float-input">Nombre</label>
        <input id="nombre" type="text" name="nombre" formControlName="nombre" pInputText>
      </div>
      <br>
      <div>
        <label for="float-input">Fecha de fabricación</label>
        <input id="fecha_fabricacion" type="date" name="fecha_fabricacion" formControlName="fecha_fabricacion" pInputText>
      </div>
      <br>
      <div>
        <label for="fecha_vencimiento">Fecha de vencimiento</label>
        <input id="fecha_vencimiento" type="date" name="fecha_vencimiento" formControlName="fecha_vencimiento" pInputText>
      </div>
      <br>
      <div>
        <label for="stock">Stock</label>
        <input id="stock" type="number" name="stock" formControlName="stock" pInputText>
      </div>
      <br>
      <div>
        <label for="valor">Valor unitario</label>
        <input id="valor" type="number" name="valor" formControlName="valor" pInputText>
      </div>
    </form>
    <ng-template pTemplate="footer">
      <button pButton pRipple label="Save" icon="pi pi-check" class="p-button-text" [disabled]="frmMedicamento.invalid" (click)="RegistrarMed()"></button>
    </ng-template>
  </p-dialog>
  <!--MODAL MODIFICAR-->
  <p-dialog header="Modificar medicamento" [responsive]="true" [style]="{width: '550px'}" [(visible)]="modalMMed" [modal]="true" styleClass="p-fluid">
    <form [formGroup]="frmMMedicamento" class="login-container" (submit)="ModificarMed()">
      <div>
        <label for="float-input">Nombre</label>
        <input id="nombre" type="text" name="nombre" formControlName="nombre" pInputText>
      </div>
      <br>
      <div>
        <label for="float-input">Fecha de fabricación</label>
        <input id="fecha_fabricacion" type="date" name="fecha_fabricacion" formControlName="fecha_fabricacion" pInputText>
      </div>
      <br>
      <div>
        <label for="fecha_vencimiento">Fecha de vencimiento</label>
        <input id="fecha_vencimiento" type="date" name="fecha_vencimiento" formControlName="fecha_vencimiento" pInputText>
      </div>
      <br>
      <div>
        <label for="stock">Stock</label>
        <input id="stock" type="number" name="stock" formControlName="stock" pInputText>
      </div>
      <br>
      <div>
        <label for="valor">Valor unitario</label>
        <input id="valor" type="number" name="valor" formControlName="valor" pInputText>
      </div>
    </form>
    <ng-template pTemplate="footer">
      <button pButton pRipple label="Save" icon="pi pi-check" class="p-button-text" [disabled]="frmMMedicamento.invalid" (click)="ModificarMed()"></button>
    </ng-template>
  </p-dialog>
  <!--MODAL VENTA-->
  <p-dialog header="Realizar venta" [responsive]="true" [style]="{width: '550px'}" [(visible)]="modalVenta" [modal]="true" styleClass="p-fluid">
    <p-messages [(value)]="msgs"></p-messages>
    <div>
      <label for="cantidad">Cantidad</label>
      <input id="cantidad" type="number" name="cantidad" [(ngModel)]="venta.cantidad" pInputText>
    </div>
    <p>Total a pagar {{precio * venta.cantidad}}</p>
    <ng-template pTemplate="footer">
      <button pButton pRipple label="Save" icon="pi pi-check" class="p-button-text" (click)="VenderMed()"></button>
    </ng-template>
  </p-dialog>

</div>
