import { Component, OnInit } from '@angular/core';
import { Medicamento } from 'src/app/models/medicamento';
import { MedicamentoService } from 'src/app/services/medicamento.service';
import { FormGroup, Validators, FormBuilder } from '@angular/forms';

@Component({
  selector: 'app-medicamento',
  templateUrl: './medicamento.component.html',
  styleUrls: ['./medicamento.component.css']
})
export class MedicamentoComponent implements OnInit{

  medicamentos: Medicamento[] = [];
  cols: any[] = [];
  modalMed: boolean = false;
  frmMedicamento: any;


  constructor(private medicamentoService: MedicamentoService,  private formBuilder: FormBuilder) {}

  ngOnInit(): void {
    this.getMedicamentos();
    this.cols = [
      {fields: "id", header: "Id"},
      {fields: "nombre", header: "Nombre"},
      {fields: "fecha_fabricacion", header: "Fecha de fabricacion"},
      {fields: "fecha_vencimiento", header: "Fecha de vencimiento"},
      {fields: "stock", header: "Cantidad"},
      {fields: "valor", header: "Valor Unitario"},
    ]
    this.frmMedicamento = this.formBuilder.group({
      nombre: ['', [Validators.required, Validators.minLength(3)]],
      fecha_fabricacion: ['', [Validators.required]],
      fecha_vencimiento: ['', [Validators.required]],
      stock: ['', [Validators.required, Validators.min(1)]],
      valor: ['', [Validators.required, Validators.min(100)]],
    });
  }

  getMedicamentos(){
    this.medicamentoService.getMedicamentos().subscribe( (result): any =>{
      let meds: Medicamento[] = [];
      for (const element of result) {
        element.fecha_fabricacion = this.formDate(element.fecha_fabricacion);
        element.fecha_vencimiento = this.formDate(element.fecha_vencimiento);
        meds.push(element);
      }
      this.medicamentos = meds;
      console.log(this.medicamentos);
    },
    error => {
      console.log(error);
    }
    );
  }

  showModalMed(): void{
    this.modalMed = true;
  }

  RegistrarMed(): void{

  }

  formDate(date: any) {
    let fecha = new Date(date),
        month = '' + (fecha.getMonth() + 1),
        day = '' + fecha.getDate(),
        year = fecha.getFullYear();
    if (month.length < 2)
        month = '0' + month;
    if (day.length < 2)
        day = '0' + day;
    return [year, month, day].join('-');
  }
}
<div>
  <br>
  <button pButton type="button" label="Registrar nuevo" class="p-button-primary" (click)="showModalMed()"></button>
  <br>
  <br>
  <p-table [value]="medicamentos" [columns]="cols" [paginator]="true" [rows]="3" >
    <ng-template pTemplate="header" let-columns>
        <tr>
          <th *ngFor="let col of columns" scope="col">{{col.header}}</th>
            <th scope="col">Opciones</th>
        </tr>
    </ng-template>
    <ng-template pTemplate="body" let-medicamento let-columns="columns">
        <tr>
            <td *ngFor="let col of columns" >
              <span *ngIf="col.fields === 'fecha_fabricacion' || col.fields === 'fecha_vencimiento'; then thenTemplateName else elseTemplateName"></span>
              <ng-template #thenTemplateName>{{ medicamento[col.fields] | date: 'dd/MM/yyyy'}}</ng-template>
              <ng-template #elseTemplateName>{{ medicamento[col.fields]}}</ng-template>
            </td>
            <td>
              <button pButton type="button" label="Vender" class="p-button-primary" ></button>
              <button pButton type="button" label="Editar" class="p-button-warning" ></button>
              <button pButton type="button" label="Borrar" class="p-button-danger" ></button>
            </td>
        </tr>
    </ng-template>
  </p-table>
  <p-dialog header="Registrar nuevo" [responsive]="true" [style]="{width: '550px'}" [(visible)]="modalMed" [modal]="true" styleClass="p-fluid">
    <form [formGroup]="frmMedicamento" class="login-container" (submit)="RegistrarMed()">
      <ng-template pTemplate="content">
      <div class="field">
        <label for="nombre">Nombre</label>
        <input id="nombre" type="text" name="nombre" formControlName="nombre">
      </div>
      <div>
        <label for="fecha_fabricacion">Fecha de fabricación</label>
        <input id="fecha_fabricacion" type="text" name="fecha_fabricacion" formControlName="fecha_fabricacion">
      </div>
      <div>
        <label for="fecha_vencimiento">Fecha de vencimiento</label>
        <input id="fecha_vencimiento" type="text" name="fecha_vencimiento" formControlName="fecha_vencimiento">
      </div>
      <div>
        <label for="stock">Stock</label>
        <input id="stock" type="number" name="stock" formControlName="stock">
      </div>
      <div>
        <label for="valor">Valor unitario</label>
        <input id="valor" type="number" name="valor" formControlName="valor">
      </div>
    </ng-template>
    </form>
    <ng-template pTemplate="footer">
      <button pButton pRipple label="Save" icon="pi pi-check" class="p-button-text" [disabled]="frmMedicamento.invalid"></button>
  </ng-template>
  </p-dialog>

</div>
